export default function IndustryDashboard() {
  const [columns, setColumns] = useState(4);

  const svgSize = useBreakpointValue({ base: "13px", md: "25px" });
  const titleFontSize = useBreakpointValue({
    base: "36px",
    sm: "48px",
    md: "64px",
    lg: "64px",
  });

  useEffect(() => {
    const handleResize = () => {
      const width = window.innerWidth;
      // setIsDesktop(width >= 768)
      if (width >= 992) {
        setColumns(4);
      } else if (width >= 768 && width < 992) {
        setColumns(3);
      } else {
        setColumns(2);
      }
    };

    handleResize();
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  // Create a grid layout based on the industry data
  const ref = useRef(null);
  const inView = useInView(ref, { threshold: 0.1 });

  const renderIndustryCards = () => {
    // Create a 6x5 grid to place cards
    const grid = Array(7)
      .fill()
      .map(() => Array(5).fill(null));

    // Place each industry in its position
    industriesData.forEach((industry) => {
      const [row, col] = industry.position;
      // Check if the number of columns is less than 4 and skip null/no image cards
      if (columns < 4 && !industry.name) {
        return;
      }
      if (row >= 1 && row <= 6 && col >= 1 && col <= 4) {
        grid[row][col] = industry;
      }
    });

    // Render the grid
    return (
      <Grid
        templateColumns={{
          base: "repeat(2, 1fr)",
          md: "repeat(3, 1fr)",
          lg: "repeat(4, 1fr)",
        }}
        gap={{ base: "10px", md: "15px" }}
        pb="4%"
        pt="4%" // Add padding top to create space from the header
      >
        {grid.slice(1).flatMap((row, rowIndex) =>
          row.slice(1).map((industry, colIndex) => {
            if (!industry) return null;
            // Skip rendering if name is empty, this is our placeholder
            if (!industry.name) {
              return (
                <GridItem
                  key={`${rowIndex}-${colIndex}`}
                  colSpan={1}
                  rowSpan={1}
                />
              );
            }
            const delay = (rowIndex + colIndex) * 0.5;

            return (
              <GridItem
                key={`${rowIndex}-${colIndex}`}
                colSpan={{
                  base: 1,
                  md: industry.isWide ? 2 : 1,
                }}
                rowSpan={1}
              >
                <Link
                  to={`/industries/${industry.link}`}
                  _hover={{ textDecoration: "none" }}
                >
                  <Box
                    width="100%"
                    height={{ base: "200px", md: "320px" }}
                    borderRadius="24px"
                    display="flex"
                    flexDirection="column"
                    justifyContent="space-between"
                    padding={{ base: "10px", md: "20px" }}
                    position="relative"
                    backgroundColor={industry.bgColor}
                    color={industry.textColor}
                    zIndex={2}
                    overflow="hidden"
                    animation={
                      inView
                        ? `${floatAnimation} 2s ease-in-out ${delay}s forwards`
                        : "none" // Restart animation when inView changes
                    }
                    // bg="red"
                  >
                    {/* Industry Icon */}
                    {industry.img && (
                      <Box
                        dangerouslySetInnerHTML={{
                          __html: decodeURIComponent(
                            industry.img.split(
                              "data:image/svg+xml;charset=utf-8,"
                            )[1]
                          ),
                        }}
                        position="absolute"
                        top="20px"
                        left="30px"
                        width="33px"
                        height="33px"
                      />
                    )}

                    {/* Industry Name & Description */}
                    <Box
                      position="absolute"
                      top="90px"
                      left="30px"
                      right="20px"
                    >
                      <Text
                        as="div"
                        fontSize="16px"
                        fontWeight="700"
                        wordBreak="break-word"
                        textAlign="left"
                        width="100%"
                        mt="-2%"
                        noOfLines={{ base: 2 }}
                      >
                        {industry.name}
                        <Box
                          width="18px"
                          height="3px"
                          borderRadius="2px"
                          marginTop="3px"
                          backgroundColor={industry.dashColor}
                        />
                      </Text>
                      {industry.description && (
                        <Text
                          fontSize="14px"
                          fontWeight="500"
                          lineHeight="18px"
                          wordBreak="break-word"
                          textAlign="left"
                          width="100%"
                          mt="10px"
                          color={
                            industry.bgColor === "white"
                              ? "#444444"
                              : industry.textColor
                          }
                          // color="red"
                          noOfLines={{ base: 3, md: 5, lg: 10 }}
                        >
                          {industry.description}
                        </Text>
                      )}
                    </Box>

                    {/* "Know More" Link with SVG Arrow */}
                    {industry.name && (
                      <Link to={`/industries/${industry.link}`}>
                        <Flex
                          gap="2"
                          alignItems="center"
                          position="absolute"
                          bottom={{ base: "10px", md: "20px" }}
                          left="30px"
                          // right="20px"
                          color={industry.textColor}
                        >
                          <Text
                            fontSize="14px"
                            fontWeight={{ base: "600", md: "400" }}
                            marginRight="4px"
                          >
                            Know More
                          </Text>
                          <Box
                            as="svg"
                            width="22px"
                            height="24px"
                            viewBox="0 0 22 24"
                            fill="currentColor"
                          >
                            <path d="M20.9602 13.0607C21.546 12.4749 21.546 11.5251 20.9602 10.9393L11.4143 1.3934C10.8285 0.807612 9.87876 0.807612 9.29297 1.3934C8.70719 1.97919 8.70719 2.92893 9.29297 3.51472L17.7783 12L9.29297 20.4853C8.70719 21.0711 8.70719 22.0208 9.29297 22.6066C9.87876 23.1924 10.8285 23.1924 11.4143 22.6066L20.9602 13.0607ZM0.100586 13.5L19.8996 13.5V10.5L0.100586 10.5V13.5Z" />
                          </Box>
                        </Flex>
                      </Link>
                    )}
                  </Box>
                </Link>
              </GridItem>
            );
          })
        )}
      </Grid>
    );
  };

  return (
    <>
      <Helmet>
        <title>Explore All Industries We Serve with Computer Vision AI</title>
        <meta
          name="description"
          content="Explore how our AI-powered video surveillance enhances safety, compliance and efficiency across transportation, retail, manufacturing and more industries."
        />
      </Helmet>
      <PageContentWrapper>
        <Box
          textAlign="center"
          backgroundColor="#E7E7E7"
          maxWidth="100%"
          minHeight={{ base: "auto", md: "1673px" }}
          position="relative"
          mx="auto"
          pb={{ base: "50px", md: "0" }}
          _before={{
            content: '""',
            position: "absolute",
            top: "22%",
            left: "-5%",
            width: "100%",
            height: "100%",
            backgroundImage: `url("./assets/image7.png")`,
            backgroundPosition: "left top",
            backgroundRepeat: "no-repeat",
            backgroundSize: "1138px",
            opacity: "1",
            zIndex: "0",
          }}
          _after={{
            content: '""',
            position: "absolute",
            left: { lg: "15%" },
            top: "-5%",
            transform: {
              base: "translateX(-50%)",
              md: "translateX(-50%)",
              lg: "none",
            },
            width: { base: "200px", md: "300px", lg: "408px" },
            height: { base: "200px", md: "300px", lg: "408px" },
            borderRadius: "50%",
            opacity: "0.12",
            background: "#3F77A5",
            filter: "blur(56.6px)",
            zIndex: "1",
          }}
        >
          <Box
            display="flex"
            flexDirection="column"
            textAlign="left"
            pt={{ base: "40px", md: "70px" }}
            zIndex={2}
            position="relative"
          >
            {/* Title */}
            <HeadingAnimation>
              <Text
                fontSize={titleFontSize}
                fontWeight="600"
                marginBottom="30px"
                lineHeight="81px"
                width="90%"
              >
                Transforming Industries with{" "}
                <Text as="span" color="#3F77A5">
                  AI-Powered{" "}
                </Text>
                <Text as="span" color="#db7b3a">
                  Intelligence
                </Text>
              </Text>
            </HeadingAnimation>
            <Flex
              w="50%"
              gap="8px"
              justifyContent="center"
              alignItems="flex-start"
            >
              <Box mt="4px">
                <svg
                  width={svgSize}
                  height={svgSize}
                  viewBox="0 0 33 33"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M30 33C31.6569 33 33 31.6569 33 30V3C33 1.34315 31.6569 0 30 0C28.3431 0 27 1.34315 27 3V27H3C1.34315 27 0 28.3431 0 30C-4.76837e-07 31.6569 1.34315 33 3 33H30ZM2.87868 7.12132L27.8787 32.1213L32.1213 27.8787L7.12132 2.87868L2.87868 7.12132Z"
                    fill="#3F77A5"
                  />
                </svg>
              </Box>
              <Text
                color="#444444"
                fontWeight="500"
                textAlign="left"
                fontSize={{ base: "14px", md: "16px" }}
                width="100%"
                lineHeight="20px"
              >
                We provide AI-driven solutions that revolutionize industries
                with advanced security, automation, and intelligence. Enhancing
                efficiency and innovation, our technology adapts to diverse
                industry needs for a smarter future.
              </Text>
            </Flex>
          </Box>

          {/* Industry Grid */}
          {renderIndustryCards()}
        </Box>
        <CtaBanner href="/industry-demo">
          Modernize Your Industry's Future with Bold
          <br />
          Surveillance
        </CtaBanner>
      </PageContentWrapper>
    </>
  );
}
